import React, { Component } from 'react'

export default class PhoneInfo extends Component {
    static defaultProps = {
        info : {
            name : '이름',
            phone : '010-1234-5678',
            id: 0
        }
    }

    //update 
    state = {
        //수정버튼을 눌렀을 때 edting 값을 true로 설정
        //이 값이 true일때, 기존에 텍스트 형태로 보여주던 값들을 input 형태로 보여주게 됨.
        editing : false,
        //input의 값은 유동적이겠지요? input 값을 담기 위해서 각 필드를 위한 값도 설정
        name : '',
        phone : ''
    }

    handleRemove = () => {
        //삭제버튼이 클릭되면 onRemove에 id 넣어서 호출
        const {info, onRemove} = this.props;
        onRemove(info.id);
    }

    handleToggleEdit = () => {
        const {editing} = this.state;
        this.setState({
            editing : !editing
        });
    }



    render() {
        const style = {
            border : '1px solid #000',
            padding : '8px',
            margin : '8px'
        }
        const {name, phone} = this.props.info;
        return (
            <ul style={style}>
                <li><b>{name}</b></li>
                <li>{phone}</li>
                <button onClick={this.handleRemove} className="btn-small d-gray">삭제</button>
                <button onClick={this.handleEdit} className="btn-small blue">수정</button>
            </ul>
        )
    }
}
